const cid = require('../index.js')

const LENGTH = 26
const COUNT = 1000000
const tmp = []
const map = {}
const count = {}

const t1 = Date.now()

console.log('正在采集数据:')

for (let i = 0; i < COUNT; i++) {
    tmp.push(cid(LENGTH))
}

const t2 = Date.now()
const t3 = (t2 - t1) / 1000
console.log('length:', tmp.length)
console.log('time:', t3)

console.log('数据采集完毕正在分析:')

tmp.map(id => {
    if (map[id]) {
        count[id] ? count[id] += 1 : count[id] = 2
    }
    else {
        map[id] = 1
    }
})

console.log('length:', tmp.length)
console.log('noRepeat:', COUNT - Object.keys(map).length)
console.log('countLength:', Object.keys(count).length)
