/// <reference types="node" />
import { Session } from 'inspector';
/**
 * Location 工具类
 */
declare class LocationUtil {
    static instance: any;
    session: Session;
    PREFIX: string;
    scripts: {};
    post$: any;
    constructor();
    init(): void;
    /**
     * 获得脚本位置
     * @param target
     */
    scriptPath(target: any): Promise<{
        column: any;
        line: any;
        path: string;
        source: string;
    }>;
    /**
     * 清除
     */
    clean(): Promise<void>;
}
declare const _default: LocationUtil;
export default _default;
